import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import HorizontalNavbar from "./Components/HorizontalNavbar";
import Sidebar from "./Components/Sidebar";
import Home from "./Components/Home";
import About from "./Components/About";
import Registration from "./Components/Registration";
import Contact from "./Components/Contact";
import Login from "./Components/Login";
import Welcome from "./Components/Welcome";
import Configure from "./Components/Configure";
import VehicleDetails from "./Components/VehicleDetails";
import Invoice from "./Components/Invoice";
import "./App.css";

function App() {
    const [email, setEmail] = useState(sessionStorage.getItem("email") || '');  // User email state from sessionStorage
    const [isLoggedIn, setIsLoggedIn] = useState(!!sessionStorage.getItem("email"));  // Check if user is logged in
    const navigate = useNavigate();

    useEffect(() => {
        // Listen for changes in sessionStorage for multi-tab synchronization (if needed)
        const checkSession = () => {
            setIsLoggedIn(!!sessionStorage.getItem("email"));  // Update login status based on sessionStorage
            setEmail(sessionStorage.getItem("email") || '');  // Update email from sessionStorage
        };

        window.addEventListener("storage", checkSession);  // Listen for sessionStorage changes

        return () => window.removeEventListener("storage", checkSession);  // Cleanup listener
    }, []);

    const handleLogout = () => {
        const confirmLogout = window.confirm("Are you sure you want to logout?");
        if (confirmLogout) {
            sessionStorage.clear();  // Clear sessionStorage
            setIsLoggedIn(false);  // Update login status
            setEmail('');  // Clear email
            navigate("/login");  // Navigate to login page without reloading the page
        }
    };

    return (
        <Router>
            <div className="App">
                <HorizontalNavbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
                <div className="main-container">
                    <Sidebar />
                    <div className="content">
                        <Routes>
                            <Route path="/" element={<Home />} />
                            <Route path="/about" element={<About />} />
                            <Route path="/registration" element={<Registration />} />
                            <Route path="/contact" element={<Contact />} />
                            <Route path="/login" element={<Login setEmail={setEmail} />} />

                            {isLoggedIn ? (
                                <>
                                    <Route path="/welcome" element={<Welcome />} />
                                    <Route path="/configure" element={<Configure />} />
                                    <Route path="/vehicle-details" element={<VehicleDetails />} />
                                    <Route path="/invoice" element={<Invoice />} />
                                </>
                            ) : (
                                // Redirect to login page for non-logged-in users
                                <Route path="*" element={<Navigate to="/login" />} />
                            )}
                        </Routes>
                    </div>
                </div>
            </div>
        </Router>
    );
}

export default App;
