import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Invoice.css";

const Invoice = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedAlternates = {}, vehicleDetails = {}, totalPrice = 0 } = location.state || {};

  const {
    manufacturer = {},
    modName = "Unknown Model",
    segment = {},
    quantity = 1,
    components = [],
  } = vehicleDetails;

  const manufacturerName = manufacturer?.manuName || "Unknown Manufacturer";
  const segmentName = segment?.segName || "Unknown Segment";

  // Categorize components
  const nonConfigurableComponents = components.filter(
    (comp) => comp.isConfigurable?.trim() === "N"
  );
  const configurableComponents = components.filter(
    (comp) => comp.isConfigurable?.trim() === "Y"
  );

  return (
    <div className="invoice">
      <h2>Invoice</h2>

      {/* Vehicle Details */}
      <div className="vehicle-details">
        <p><strong>Manufacturer:</strong> {manufacturerName}</p>
        <p><strong>Model:</strong> {modName}</p>
        <p><strong>Segment:</strong> {segmentName}</p>
        <p><strong>Quantity:</strong> {quantity}</p>
        <p><strong>Total Price:</strong> Rs.{totalPrice.toFixed(2)}</p>
      </div>

      {/* Component List */}
      <h3>Components</h3>
      <div className="components-list">
        <h4>Non-Configurable Components</h4>
        <ul>
          {nonConfigurableComponents.length > 0 ? (
            nonConfigurableComponents.map((comp, index) => (
              <li key={index}>{comp.component?.componentName || "Unknown Component"}</li>
            ))
          ) : (
            <li>No non-configurable components.</li>
          )}
        </ul>

        <h4>Configurable Components</h4>
        <ul>
          {configurableComponents.length > 0 ? (
            configurableComponents.map((comp, index) => {
              const selectedAlt = selectedAlternates[comp.component.id];
              return (
                <li key={index}>
                  {selectedAlt
                    ? selectedAlt.altComponent?.componentName
                    : comp.component?.componentName || "Unknown Component"}
                </li>
              );
            })
          ) : (
            <li>No configurable components.</li>
          )}
        </ul>
      </div>

      {/* Buttons */}
      <div className="buttons">
        <button onClick={() => navigate(-1)} className="button back-btn">Go Back</button>
        <button onClick={() => navigate("/confirm-order")} className="button proceed-btn">Proceed with Order</button>
      </div>
    </div>
  );
};

export default Invoice;
