import React, { useState, useEffect, useMemo } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Configure.css";

const Configure = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { vehicleDetails = {}, components = [], quantity = 1 } = location.state || {};

  // Memoized non-configurable components
  const nonConfigurableComponents = useMemo(
    () => components?.filter((comp) => comp?.isConfigurable?.trim() === "N") || [],
    [components]
  );

  const [filteredComponents, setFilteredComponents] = useState(nonConfigurableComponents);
  const [altComponentsMap, setAltComponentsMap] = useState({});
  const [showStandardSection, setShowStandardSection] = useState(false);
  const [showInteriorSection, setShowInteriorSection] = useState(false);
  const [showExteriorSection, setShowExteriorSection] = useState(false);
  const [standardComponents, setStandardComponents] = useState([]);
  const [interiorComponents, setInteriorComponents] = useState([]);
  const [exteriorComponents, setExteriorComponents] = useState([]);
  const [selectedAlternates, setSelectedAlternates] = useState({});
  const [totalPrice, setTotalPrice] = useState(vehicleDetails.price * quantity); // base price

  // Update filtered components when non-configurable list changes
  useEffect(() => {
    setFilteredComponents(nonConfigurableComponents);
  }, [nonConfigurableComponents]);

  // Safe destructuring with fallbacks
  const { modName = "Unknown Model", imagePath = "", manufacturer = {}, segment = {} } = vehicleDetails;
  const manufacturerName = manufacturer?.manuName || "Unknown Manufacturer";
  const segmentName = segment?.segName || "Unknown Segment";
  const imageUrl = imagePath && imagePath.startsWith("/") ? process.env.PUBLIC_URL + imagePath : imagePath;

  // Update the total price whenever selected alternates change
  useEffect(() => {
    let updatedPrice = vehicleDetails.price * quantity; // base price
    Object.keys(selectedAlternates).forEach((key) => {
      updatedPrice += selectedAlternates[key].priceVariation || 0; // add price variation for selected alternates
    });
    setTotalPrice(updatedPrice);
  }, [selectedAlternates, quantity, vehicleDetails.price]);

  const handleConfirm = () => {
    navigate("/invoice", {
      state: {
        selectedAlternates,
        vehicleDetails,
        totalPrice,
        nonConfigurableComponents: filteredComponents,  // Pass non-configurable components
        standardComponents,
        interiorComponents,
        exteriorComponents
      }
    });
  };
  
  const handleModify = () => navigate("/welcome");

  // Fetch configurable components with their alternate mappings
  const fetchComponents = async (compType) => {
    try {
      const modelId = vehicleDetails.id;
      if (!modelId) {
        console.error("Model ID is undefined.");
        return;
      }

      const response = await fetch(`http://localhost:8080/api/components/${modelId}`);
      if (!response.ok) throw new Error("Failed to fetch component details");

      const data = await response.json();

      // Filter components based on compType
      const filteredComponents = data.vehicleComponents?.filter(
        (comp) => comp.compType === compType && comp.isConfigurable?.trim() === "Y"
      ) || [];

      // Set the corresponding components state based on the button clicked
      if (compType === "S") {
        setStandardComponents(filteredComponents);
        setShowStandardSection(true);
      } else if (compType === "I") {
        setInteriorComponents(filteredComponents);
        setShowInteriorSection(true);
      } else if (compType === "E") {
        setExteriorComponents(filteredComponents);
        setShowExteriorSection(true);
      }

      // Map each component to its alternate components
      const altMap = {};
      filteredComponents.forEach((comp) => {
        if (comp.component?.id) {
          altMap[comp.component.id] = data.alternateComponents?.filter(
            (alt) => alt.component?.id === comp.component.id
          ) || [];
        }
      });

      setAltComponentsMap(altMap);
    } catch (error) {
      console.error("Error fetching component details:", error);
    }
  };

  // Handle alternate component selection
  const handleAlternateSelect = (compId, altId, priceVariation) => {
    setSelectedAlternates((prev) => {
      const updated = { ...prev, [compId]: { altId, priceVariation } };
      return updated;
    });
  };

  return (
    <div className="configure">
      <h2>Vehicle Configuration</h2>

      <div className="vehicle-info">
        <div className="vehicle-image">
          {imageUrl ? (
            <img src={imageUrl} alt={`${manufacturerName} ${modName}`} className="vehicle-img" />
          ) : (
            <p>No Image Available</p>
          )}
        </div>
        <div className="vehicle-info-text">
          <h3>{manufacturerName} - {modName}</h3>
          <p><strong>Segment:</strong> {segmentName}</p>
          <p><strong>Variant:</strong> {modName}</p>
          <p><strong>Quantity:</strong> {quantity}</p>
          <p><strong>Total Price:</strong> Rs.{totalPrice.toFixed(2)}</p> {/* Display the dynamic total price */}
        </div>
      </div>

      {/* Non-Configurable Components */}
      <h3>Non-Configurable Components</h3>
      <table>
        <thead>
          <tr>
            <th>Component Name</th>
          </tr>
        </thead>
        <tbody>
          {filteredComponents.length === 0 ? (
            <tr><td colSpan="1">No components available.</td></tr>
          ) : (
            filteredComponents.map((comp, index) => (
              <tr key={index}>
                <td>{comp.component?.componentName || "Unknown Component"}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Standard Configurable Components */}
      {showStandardSection && (
        <div className="standard-section">
          <h3>Standard Configurable Components</h3>
          {standardComponents.length === 0 ? (
            <p>No standard configurable components available.</p>
          ) : (
            standardComponents.map((comp) => (
              <div key={comp.component.id} className="standard-component">
                <label>{comp.component.componentName}</label>
                <select
                  onChange={(e) => {
                    const selectedAlt = altComponentsMap[comp.component.id]?.find(
                      (alt) => alt.altComponent?.id === parseInt(e.target.value)
                    );
                    handleAlternateSelect(
                      comp.component.id,
                      selectedAlt?.altComponent?.id,
                      selectedAlt?.priceVariation || 0
                    );
                  }}
                >
                  <option value="">Select Alternate</option>
                  {altComponentsMap[comp.component.id]?.length > 0 ? (
                    altComponentsMap[comp.component.id]?.map((alt, altIndex) => (
                      <option key={altIndex} value={alt.altComponent?.id}>
                        {alt.altComponent?.componentName} (Rs.{alt.priceVariation?.toFixed(2)})
                      </option>
                    ))
                  ) : (
                    <option disabled>No Alternates Available</option>
                  )}
                </select>
              </div>
            ))
          )}
        </div>
      )}

      {/* Interior Configurable Components */}
      {showInteriorSection && (
        <div className="interior-section">
          <h3>Interior Configurable Components</h3>
          {interiorComponents.length === 0 ? (
            <p>No interior configurable components available.</p>
          ) : (
            interiorComponents.map((comp) => (
              <div key={comp.component.id} className="interior-component">
                <label>{comp.component.componentName}</label>
                <select
                  onChange={(e) => {
                    const selectedAlt = altComponentsMap[comp.component.id]?.find(
                      (alt) => alt.altComponent?.id === parseInt(e.target.value)
                    );
                    handleAlternateSelect(
                      comp.component.id,
                      selectedAlt?.altComponent?.id,
                      selectedAlt?.priceVariation || 0
                    );
                  }}
                >
                  <option value="">Select Alternate</option>
                  {altComponentsMap[comp.component.id]?.length > 0 ? (
                    altComponentsMap[comp.component.id]?.map((alt, altIndex) => (
                      <option key={altIndex} value={alt.altComponent?.id}>
                        {alt.altComponent?.componentName} (Rs.{alt.priceVariation?.toFixed(2)})
                      </option>
                    ))
                  ) : (
                    <option disabled>No Alternates Available</option>
                  )}
                </select>
              </div>
            ))
          )}
        </div>
      )}

      {/* Exterior Configurable Components */}
      {showExteriorSection && (
        <div className="exterior-section">
          <h3>Exterior Configurable Components</h3>
          {exteriorComponents.length === 0 ? (
            <p>No exterior configurable components available.</p>
          ) : (
            exteriorComponents.map((comp) => (
              <div key={comp.component.id} className="exterior-component">
                <label>{comp.component.componentName}</label>
                <select
                  onChange={(e) => {
                    const selectedAlt = altComponentsMap[comp.component.id]?.find(
                      (alt) => alt.altComponent?.id === parseInt(e.target.value)
                    );
                    handleAlternateSelect(
                      comp.component.id,
                      selectedAlt?.altComponent?.id,
                      selectedAlt?.priceVariation || 0
                    );
                  }}
                >
                  <option value="">Select Alternate</option>
                  {altComponentsMap[comp.component.id]?.length > 0 ? (
                    altComponentsMap[comp.component.id]?.map((alt, altIndex) => (
                      <option key={altIndex} value={alt.altComponent?.id}>
                        {alt.altComponent?.componentName} (Rs.{alt.priceVariation?.toFixed(2)})
                      </option>
                    ))
                  ) : (
                    <option disabled>No Alternates Available</option>
                  )}
                </select>
              </div>
            ))
          )}
        </div>
      )}

      {/* Buttons for filtering components */}
      <div className="filter-buttons">
        <button onClick={() => fetchComponents("S")} className="button standard-btn">Standard</button>
        <button onClick={() => fetchComponents("I")} className="button interior-btn">Interior</button>
        <button onClick={() => fetchComponents("E")} className="button exterior-btn">Exterior</button>
      </div>

      {/* Bottom buttons */}
      <div className="buttons">
        <button onClick={handleConfirm} className="button confirm-btn">Confirm</button>
        <button onClick={handleModify} className="button modify-btn">Modify</button>
      </div>
    </div>
  );
};

export default Configure;
