import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./Invoice.css";

const Invoice = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const {
    selectedAlternates = {},
    vehicleDetails = {},
    totalPrice = 0,
    nonConfigurableComponents = [],
    standardComponents = [],
    interiorComponents = [],
    exteriorComponents = [],
  } = location.state || {};

  const { modName = "Unknown Model", manufacturer = {}, segment = {} } = vehicleDetails;
  const manufacturerName = manufacturer?.manuName || "Unknown Manufacturer";
  const segmentName = segment?.segName || "Unknown Segment";

  const handleBack = () => navigate("/configure");

  // Merge all components into one list
  const allComponents = [
    ...nonConfigurableComponents.map(comp => comp.component?.componentName || "Unknown Component"),
    ...standardComponents.map(comp => selectedAlternates[comp.component.id]?.altComponentName || comp.component.componentName),
    ...interiorComponents.map(comp => selectedAlternates[comp.component.id]?.altComponentName || comp.component.componentName),
    ...exteriorComponents.map(comp => selectedAlternates[comp.component.id]?.altComponentName || comp.component.componentName)
  ];

  // Calculate total price with 18% GST
  const gstAmount = totalPrice * 0.18;
  const totalWithGST = totalPrice + gstAmount;

  // Function to generate PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    
    // Title
    doc.setFontSize(18);
    doc.text("Vehicle Invoice", 14, 20);

    // Vehicle Details
    doc.setFontSize(12);
    doc.text(`Manufacturer: ${manufacturerName}`, 14, 30);
    doc.text(`Model: ${modName}`, 14, 40);
    doc.text(`Segment: ${segmentName}`, 14, 50);
    doc.text(`Total Price (Excl. GST): Rs.${totalPrice.toFixed(2)}`, 14, 60);

    // Components List
    doc.text("Selected Components:", 14, 75);
    doc.autoTable({
      startY: 80,
      head: [["Component Name"]],
      body: allComponents.map(comp => [comp]),
    });

    // Price Breakdown
    const finalY = doc.lastAutoTable.finalY || 90; // Get last Y position of table
    doc.text(`Total Price (Excl. GST): Rs.${totalPrice.toFixed(2)}`, 14, finalY + 10);
    doc.text(`GST (18%): Rs.${gstAmount.toFixed(2)}`, 14, finalY + 20);
    doc.text(`Total Price (Incl. GST): Rs.${totalWithGST.toFixed(2)}`, 14, finalY + 30);

    // Save PDF as Blob and store in localStorage
    const pdfBlob = doc.output("blob");
    const reader = new FileReader();
    reader.readAsDataURL(pdfBlob);
    reader.onloadend = () => {
      localStorage.setItem("invoicePDF", reader.result);
    };

    doc.save("Vehicle_Invoice.pdf");
  };

 

  return (
    <div className="invoice">
      {/* Section 1: Vehicle Details & Base Price */}
      <div className="section">
        <h2>Invoice</h2>
        <p><strong>Manufacturer:</strong> {manufacturerName}</p>
        <p><strong>Model:</strong> {modName}</p>
        <p><strong>Segment:</strong> {segmentName}</p>
       
      </div>

      {/* Section 2: Components */}
      <div className="section">
        <h2>Selected Components</h2>
        <ul>
          {allComponents.length === 0 ? <li>No components selected.</li> : allComponents.map((comp, index) => (
            <li key={index}>{comp}</li>
          ))}
        </ul>
      </div>

      {/* Section 3: Price Breakdown with GST */}
      <div className="section">
        
        <p><strong>Total Price (Excl. GST):</strong> Rs.{totalPrice.toFixed(2)}</p>
        <p><strong>GST (18%):</strong> Rs.{gstAmount.toFixed(2)}</p>
        <p><strong>Total Price (Incl. GST):</strong> Rs.{totalWithGST.toFixed(2)}</p>
      </div>

      {/* Buttons */}
      <button onClick={handleBack} className="button back-btn">Back to Configuration</button>
      <button onClick={generatePDF} className="button pdf-btn">Download PDF</button>
     
    </div>
  );
};

export default Invoice;
