import React, { useState } from "react";
import { loginUser } from "../services/api";

const Login = ({ setToken }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("Login button clicked!");  // ✅ Debugging log

        if (!email || !password) {
            console.log("Missing email or password");  // ✅ Ensure both fields are filled
            setError("Please enter both email and password.");
            return;
        }

        setLoading(true);
        setError(null);

        try {
            console.log("Sending request with:", { email, password });  // ✅ Log credentials before API call

            const response = await loginUser(email, password);

            if (response && response.token) {
                console.log("Login successful! Token:", response.token);  // ✅ Verify successful login
                setToken(response.token);
                sessionStorage.setItem("token", response.token);
            } else {
                throw new Error("Invalid credentials. Try again.");
            }
        } catch (error) {
            console.error("Login error:", error);
            setError(error.message || "An unexpected error occurred.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit" disabled={loading}>
                    {loading ? "Logging in..." : "Login"}
                </button>
            </form>
        </div>
    );
};

export default Login;
