const API_BASE_URL = "http://localhost:8083";  // Change this URL to your actual backend URL

export const loginUser = async (email, password) => {
    try {
        const response = await fetch(`${API_BASE_URL}/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',  // Indicating JSON body content
            },
            body: JSON.stringify({ email, password }),  // Send the email and password in the request body
        });

        if (!response.ok) {
            const errorText = await response.text();  // Get error message from response
            throw new Error(`Login failed: ${errorText}`);  // Throw error if not ok
        }

        const companyInfo = await response.json();  // Get the CompanyInfo object from the response
        if (companyInfo) {
            return companyInfo;  // Return the CompanyInfo object
        } else {
            throw new Error('Invalid credentials');  // Throw error if no valid response
        }
    } catch (error) {
        console.error("Error during login:", error);
        return null;  // Return null if error occurs
    }
};
