import React, { useState } from 'react';
import { loginUser } from '../services/api';  // Importing the login helper API function

const Login = ({ setEmail }) => {
    const [email, setEmailInput] = useState('');  // User email state
    const [password, setPassword] = useState('');  // User password state
    const [error, setError] = useState('');  // Error state for invalid credentials
    const [loading, setLoading] = useState(false);  // Loading state for button

    const handleLogin = async (e) => {
        e.preventDefault();  // Prevent form default behavior
        setLoading(true);
        setError('');  // Reset any existing error message

        try {
            // Call API to login
            const response = await loginUser(email, password);

            if (response) {
                // On success, store the CompanyInfo object in sessionStorage
                sessionStorage.setItem('companyInfo', JSON.stringify(response));

                // Optionally, you can update the email in App state
                setEmail(response.email);

                // Redirect to the welcome page (or home page)
                window.location.href = '/welcome';  // or use `navigate("/welcome")` if using `useNavigate` from `react-router-dom`
            } else {
                setError('Invalid credentials');  // Handle error if credentials are wrong
            }
        } catch (error) {
            setError('Login failed. Please try again.');
        } finally {
            setLoading(false);  // Turn off the loading state
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Show error message */}
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmailInput(e.target.value)}  // Update email state
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}  // Update password state
                    required
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Logging in...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
